import Project from "../components/Project";
import {projects} from "../helpers/ProjectList";
import { useState} from "react";
import Pagination from "../components/Pagination";


function Projects(props) {
    // const [myProject , setMyProject] = useState(projects)
    const [currentPage, setCurrentPage] = useState(1)
    const [projectPerPage] = useState(2)

const lastProjectIndex = currentPage * projectPerPage
    const firstProjectIndex = lastProjectIndex - projectPerPage
    const currentProject = projects.slice(firstProjectIndex , lastProjectIndex)
console.log(lastProjectIndex , firstProjectIndex , currentProject)

    const paginate = (pageNumber) => {
      setCurrentPage(pageNumber)
    }

    return (
        <div>
            <main className="section">
                <div className="container">
                    <h2 className="title-1">Projects</h2>
                    <ul className="projects"  >





                        {currentProject.map((project)=>(

                                <Project title={project.title} img={project.img} id={project.id}  />
                            )
                            )
                        }






                    </ul>
                    <Pagination projectPerPage={projectPerPage} totalProjects={projects.length} paginiate={paginate}/>
                </div>
            </main>
        </div>
    );
}

export default Projects;
